package com.projet.securite.domain;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.*;import java.io.Serializable;import java.util.ArrayList;import java.util.Arrays;import java.util.Collection;@Entity@Data@NoArgsConstructor@AllArgsConstructorpublic class User implements Serializable {    @Id @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    private String nom;    private String prenom;    private String username;    @JsonProperty(access = JsonProperty.Access.WRITE_ONLY)    private String password;    @ManyToMany(fetch = FetchType.EAGER)    private Collection<Role> roles = new ArrayList<>();}